package com.leetcode.crackthecodes.solutions.medium;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.*;

class P0005_LongestPalindromeTest {

    private P0005_LongestPalindrome p0005_longestPalindrome = new P0005_LongestPalindrome();

    @ParameterizedTest
    @MethodSource({ "testCases" })
    public void testCompression(String input,String expected) {
        Assertions.assertEquals(expected, p0005_longestPalindrome.longestPalindrome(input));
    }

    static Stream<Arguments> testCases() {

        return Stream.of(
                Arguments.of("babad","bab"),
                Arguments.of("cbbd","bb"),
                Arguments.of("ac","a"),
                Arguments.of("",""),
                Arguments.of("","xrcrx")
                 );
    }
}